import React, { useState } from "react";
import { ChevronDown, ChevronRight, Lock } from "lucide-react";

const tabs = ["Overview", "Curriculum", "Instructor"];

const curriculumData = [
  {
    title: "Introduction",
    duration: "3 Lectures, 34:51 min",
    content: {
      level: "Intermediate Level",
      description:
        "This tutorial will help you learn quickly and thoroughly. Lorem ipsum is dummy text used in laying out print, graphic or web designs.",
      resources: "2 Videos, 1 Audio, 1 Notebook",
      lectures: [
        { type: "video", title: "Getting Started and Introductions", preview: true },
        { type: "video", title: "Getting Started and Introductions", preview: true },
        { type: "audio", title: "Connectig Through Technology" },
        { type: "video", title: "Connectig Through Technology" },
      ],
    },
  },
  {
    title: "Getting Started",
    duration: "3 Lectures, 34:51 min",
  },
  {
    title: "Start from Sketch Design",
    duration: "3 Lectures, 34:51 min",
  },
  {
    title: "Getting Help & Support in Seller Central",
    duration: "3 Lectures, 34:51 min",
  },
];

const CourseDetailsPage = () => {
  const [activeTab, setActiveTab] = useState("Overview");
  const [openIndex, setOpenIndex] = useState(0);
  const [showVideo, setShowVideo] = useState(null);

  const handleVideoClick = (id) => {
    setShowVideo(id === showVideo ? null : id);
  };

  return (
    <div className="max-w-screen-xl mx-auto p-6 grid grid-cols-1 lg:grid-cols-3 gap-10">
      <div className="lg:col-span-2 space-y-6">
        <img
          src="/course-hero.jpg"
          alt="Course Preview"
          className="rounded-2xl w-full object-cover"
        />
        <span className="bg-teal-600 text-white text-xs font-semibold px-3 py-1 rounded-full inline-block">
          Data Science
        </span>
        <h1 className="text-3xl font-bold text-gray-900">
          UI/UX Design and Graphics Learning Bootcamp 2022
        </h1>

        <section>
          <div className="mt-10">
            <div className="flex space-x-6 border-b pb-2">
              {tabs.map((tab) => (
                <button
                  key={tab}
                  className={`text-sm font-medium pb-2 ${
                    activeTab === tab
                      ? "text-white bg-rose-500 px-4 py-1 rounded"
                      : "text-gray-600 hover:text-gray-900"
                  }`}
                  onClick={() => setActiveTab(tab)}
                >
                  {tab}
                </button>
              ))}
            </div>

            {activeTab === "Overview" && (
              <div className="mt-6">
                <h2 className="text-xl font-semibold mb-2">Course Description</h2>
                <p className="text-gray-700 mb-2">
                  This tutorial will help you learn quickly and thoroughly. Lorem ipsum is dummy text used in laying out print, graphic or web designs.
                </p>
                <p className="text-gray-700">
                  You'll be exposed to principles and strategies, and you'll learn how to apply them by coding three different websites.
                </p>
              </div>
            )}

            {activeTab === "Curriculum" && (
              <div className="mt-6 space-y-4">
                <div className="text-sm text-gray-600 mb-2">
                  Level <strong>Beginner</strong> | 12 Lectures | Total: 5 Hours 56 Minutes 24 Seconds
                </div>

                {curriculumData.map((section, index) => (
                  <div key={index} className="bg-white rounded-lg border">
                    <div
                      className={`flex justify-between items-center px-4 py-3 cursor-pointer ${
                        openIndex === index ? "bg-teal-500 text-white" : ""
                      }`}
                      onClick={() => setOpenIndex(openIndex === index ? null : index)}
                    >
                      <span className="font-semibold">{section.title}</span>
                      <span className="text-sm">{section.duration}</span>
                      {openIndex === index ? <ChevronDown /> : <ChevronRight />}
                    </div>

                    {openIndex === index && section.content && (
                      <div className="px-6 pb-4 pt-2 text-sm text-gray-700 space-y-3">
                        <p className="font-medium text-gray-800">{section.content.level}</p>
                        <ul className="list-disc ml-6 space-y-2">
                          <li
                            className="text-blue-600 cursor-pointer hover:underline"
                            onClick={() => handleVideoClick("azureIntro")}
                          >
                            Cloud Basics & Why Azure
                          </li>
                          {showVideo === "azureIntro" && (
                            <div className="mt-2">
                              <iframe
                                className="w-full h-64 rounded shadow-lg"
                                src="https://www.youtube.com/embed/some-azure-video"
                                title="Cloud Basics & Why Azure"
                                allowFullScreen
                              ></iframe>
                            </div>
                          )}
                          <li
                            className="text-blue-600 cursor-pointer hover:underline"
                            onClick={() => handleVideoClick("resourceGroups")}
                          >
                            Azure Portal, Resource Groups, & Subscriptions
                          </li>
                          {showVideo === "resourceGroups" && (
                            <div className="mt-2">
                              <iframe
                                className="w-full h-64 rounded shadow-lg"
                                src="https://www.youtube.com/embed/some-azure-resource-video"
                                title="Azure Resource Groups"
                                allowFullScreen
                              ></iframe>
                            </div>
                          )}
                          <li>Data Fundamentals: Structured, Semi-structured, Unstructured</li>
                          <li>Azure Storage: Blob, ADLS Gen2, File Storage</li>
                          <li>Basic Python for Data Engineers</li>
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}

            {activeTab === "Instructor" && (
              <div className="bg-white p-6 rounded-xl shadow-md mt-6">
                <div className="flex flex-col md:flex-row items-center gap-6">
                  <img
                    src="/your-image-path.jpg"
                    alt="Daniel Smith"
                    className="w-48 h-48 object-cover rounded-lg"
                  />
                  <div className="flex-1">
                    <h2 className="text-2xl font-bold text-gray-900">Daniel Smith</h2>
                    <p className="text-pink-500 font-medium mb-4">User Experience Designer</p>
                    <div className="grid grid-cols-2 gap-x-6 gap-y-3 mb-4">
                      <div className="flex items-center space-x-2 text-gray-700">
                        <span>📘</span>
                        <span>65+ Courses</span>
                      </div>
                      <div className="flex items-center space-x-2 text-gray-700">
                        <span>👨‍🎓</span>
